switch(Inputs.state){
    case 'active':
        Outputs.doEvalute();
    break;

    case 'inactive':
        return;
    break;
}

if(Inputs.state && Inputs.articleRatings && Inputs.userId){

    //Inputs.articleRatings = object{ state:array[], state:array[], state:array[] }
    //Inputs.articleRatings[state] = noodl.array( {user:user.id}, ... )

    const state = Inputs.state;
    let myRatings = Inputs.articleRatings;
    let ratingsArray = Noodl.Array.create({});

    //is the current user in the list of rating for any state this article has?
    let isRated = false;
    for(let x in myRatings.data){
        console.log(x);
        switch(x){
            case 'good':
                if(myRatings[x].items.filter(item => item.user == Inputs.userId).length){
                    alert('You already rated this article ' + x + '. You cannot change your rating after it has been given. In other words, no takesies backsies.');
                    isRated = true;
                }
            break;

            case 'neutral':
                if(myRatings[x].items.filter(item => item.user == Inputs.userId).length){
                    alert('You already rated this article ' + x + '. You cannot change your rating after it has been given. In other words, no takesies backsies.');
                    isRated = true;
                }
            break;

            case 'bad':
                if(myRatings[x].items.filter(item => item.user == Inputs.userId).length){
                    alert('You already rated this article ' + x + '. You cannot change your rating after it has been given. In other words, no takesies backsies.');
                    isRated = true;
                }
            break;
        }
    }

    if(!isRated){
    //does the selected state exist in db?
        if(myRatings[state] != undefined){
            //yes

            //does the selected state contain any ratings?
            if(myRatings[state].items != undefined && myRatings[state].items.length > 0){
                //yes
                ratingsArray.items = myRatings[state].items;

                //if user is not in list of ratings for this state, push user rating
                if(ratingsArray.items.filter(item => item.user == Inputs.userId).length != 1){
                    ratingsArray.items.push( Noodl.Object.create({'user':Inputs.userId}) );    
                }
            }else{
                //no ratings found for this state
                ratingsArray.items.push( Noodl.Object.create({'user':Inputs.userId}) );
                myRatings[state] = ratingsArray;
            }
        }else{
            //the state wasn't found for this article
            ratingsArray.items.push( Noodl.Object.create({'user':Inputs.userId}) );
            myRatings[state] = ratingsArray;
        }

        Outputs.result = myRatings;
    }
}

